    //Metodo para cadastrar o usuario no banco de dados
  private void cadastrarUsuario(){ 
            String usuario = txtUsuario.getText();
            String senha = txtSenha.getText();
            int tipo = cbAdmUsu.getSelectedIndex();
                   
            System.out.println(new Usuario(usuario, senha, tipo));
            
                         
    }
    
  
    //Metodo para alterar o usuario no banco de dados
    private void alterarCliente(){

        String usuario = txtUsuario.getText();
        String senha = txtSenha.getText();
        int tipo = cbAdmUsu.getSelectedIndex();
        int codigo = Integer.parseInt(txtCodigo.getText());
        Usuario cliente = new Usuario(codigo,usuario, senha, tipo);
        dao.UsuarioDAO.getInstance().update(cliente);
        
    }
    
    //Metodo para excluir o usuario no banco de dados
    private void excluirCliente(){ 
        dao.UsuarioDAO.getInstance().delete(Integer.parseInt(txtCodigo.getText()));
    }
    //Metodo para consultar o usuario no banco de dados
    //E listalos nos campos da tela
    private void consultaCliente(){
        Usuario cliente = new Usuario();
        cliente = dao.UsuarioDAO.getInstance().findByCodigo(Integer.parseInt(txtCodigo.getText()));
        txtUsuario.setText(cliente.getNome());
        txtSenha.setText(cliente.getSenha());
        cbAdmUsu.setSelectedIndex(cliente.getTipo());
            
        
    }
    
